void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offs = vec2(cos(iTime * 0.5), sin(iTime * 0.5));

	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; // adjust for aspect

    // scroll sphere around with offset
    uv += offs * 0.25;

    float t = abs(1. - dot(uv, uv));
	float z = sqrt(t);
    uv *= 2.0 - z;

	fragColor = texture(iChannel0, uv * 0.25 + offs * 0.5);

    fragColor = sqrt(fragColor * fragColor * t); // darken a bit along the seam (gamma-correct-ish)
}
