void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))*texture(iChannel0,uv).r;
	vec2 st = uv-vec2(0.5);
    st.x*= iResolution.x/iResolution.y;
    float r = 0.2+0.1*cos(atan(st.y,st.x)*7.)*texture(iChannel0,uv).r*4.;
    float circle = smoothstep(r,r-0.05,length(st));

    r = 0.05;
    st.y+= -0.3;
    col*=clamp((smoothstep(r,r-0.05,abs(st.x+sin(st.y*3.+7.13)*0.5)))*step(0.5,1.-uv.y)+circle,0.0,1.);
    // Output to screen
    fragColor = vec4(col,1.0);
}
