void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime*.1;
    vec2 uv = fragCoord/iResolution.y;
    vec2 center = vec2(1);
    center.x+=sin(uv.y*3.7+t);
    center.y+=cos(uv.x*3.7+t);
    float d = distance(uv,center);
     t += uv.y;
     t *= cos(fragCoord.x*37.123);
    float k = mod(d-t, .1 )*12.0*(.5+uv.y*.5);
    fragColor = vec4(k);
}
