//future uniform
const float uAngle = 0.5233; 


//utils
float noise(vec2 p){
    return texture(iChannel0,fract(p)).r;
}
vec2 rotate(vec2 vector,float angle){
    float s = sin(angle),
          c = cos(angle);

    return vector*mat2(c,s,-s,c);

    //r = v *|cosA   -sinA|
    //       |sinA    cosA|
}


vec4 wood(vec2 p){
    vec3 color = vec3(0.21, 0.11, 0.05);
    float m =mod(p.y+sin(p.x)/20.+sin(p.x+p.y*0.5)/10.,3.5);
    float wood = fract(pow(m-1.8,2.))+0.26;
    return m<0.1?vec4(0.03):vec4(color-vec3(0.14, 0.07, 0.0)*wood,1.);
}



//Wind funtion(with some modyfications) created by bjarkeck. Thank You so much:).
//https://www.shadertoy.com/view/ls33W7
float wind(vec2 p, in float t)
{
    p += vec2(1.4 + cos(t + p.x * 3.33), 1.5 + sin(t * 0.2 + p.y * 4.)) * 0.003;
    return mix(1.,1.9,noise(p)); 
}
vec3 grass(vec2 p){
    return mix(
        vec3(0.11,0.62,0.),
        vec3(0.10,0.47,0.),
        noise(p)
    );
}


void mainImage(out vec4 o, vec2 p)
{
    vec3 R = iResolution;
    float m = min(R.x,R.y);
    p=(p-R.xy/2.)/m;
    p.y*=-1.;
    p*=4.;
    p.y+= (iMouse.y-R.y/2.)/m*4.;
    p.x-= (iMouse.x-R.x/2.)/m*4.;


    float a = atan(p.x,p.y);
    float r = length(p);
    float ab = abs(a);


    if(r>3.25)
        o = wood(rotate(p*6.,uAngle)+3.)*1.4;//o = vec4(0.439, 0.20, 0.075,1.);
    else{
        o = vec4(vec3(0.),1.);
        float m = mod(ab,0.5233);
        if(m<0.521 && m>0.004 || r<1.9) 
            o.rgb += .5;

    	o.rgb*=grass(p)*wind(p*2.5, iTime * 2.);
        if(r<1.9) o.rgb*=0.6;
     }


    //borders
    if(abs(r-3.25)<0.01)
        o.rgb *=0.;
    else if(abs(r-1.9)<0.01){
        o.rgb *= 0.7;
    }
}
