#define clamps(x) clamp(x,0.,1.)
float rand(float co){
    return fract(sin(dot(vec2(co) ,vec2(12.9898,78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color1 = texture(iChannel0,uv).rgb;
    for (float i = 0.; i < 200.; i++) {
        vec2 position = vec2(rand(iTime+(i/3.5)),rand(iTime+20.+(i*1.2)));
        vec3 color2 = texture(iChannel1,position).rgb;
        float alpha = 1.-clamps((length(uv-position)-0.0013)*mix(80.,200.,rand(i+iTime)));
        color1 = mix(color1,color2,alpha);
    }
    fragColor = vec4(color1,1.0);
}

