void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv*2.;
    uv = uv-1.;
    uv = abs(uv); 

    uv = uv-.3;

    float angle = atan(uv.y*2.,uv.x*2.0);
    float dist = length(uv);

    dist = dist+dist*sin(dist*20.+iTime)*.5;

    uv.y = sin(angle+iTime*.5)*dist;
    uv.x = cos(angle+iTime*.1)*dist;

    uv = uv+.5;

    vec4 color = texture(iChannel0, abs(uv));
    color += texture(iChannel1, uv);

    //color += sin(color*10.+dist)*500.;


    color.rgb = sin((color.rgb+vec3(0.11,.88,.11))*6.28+iTime);


   	color = mix(color,color,uv.x);

    uv.xy = uv.xy+color.rg*.1-.1;
    fragColor = vec4(color);
}
