vec2 v;
vec3 t (int a, int b, float lod) {
	return texture(iChannel0, v+(lod+1.)*vec2(a,b)/iResolution.xy,lod).xyz;
}
void grad (in float lod, out vec2 r, out vec2 g, out vec2 b) {
    vec3 ud = (t(0,1,lod)-t(0,-1,lod));
    vec3 lr = (t(1,0,lod)-t(-1,0,lod));
    r = vec2(lr.x,ud.x);
    g = vec2(lr.y,ud.y);
    b = vec2(lr.z,ud.z);
}
vec3 l (vec2 u, float a) {
	return texture(iChannel0, v+4./(1.+length(u))*(u*sin(a)/iResolution.xy)).xyz;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    v = fragCoord/iResolution.xy;
    vec2 r,g,b,R=vec2(0),G=vec2(0),B=vec2(0);
    for (int i = 0; i < 4; i++) {
    	grad(float(i),r,g,b);
        R += r;
        G += g;
        B += b;
    }
    vec2 h = v*2.-1.;
    float T = -7.*iTime+50.*(t(0,0,3.).x);
    fragColor = vec4(vec3(l(R,T).x,l(G,T).y,l(B,T).z),1.0);
}
