vec3 colorA = vec3(0.349,0.241,0.812);
vec3 colorB = vec3(1.000,0.233,0.224);
vec3 colorC = vec3(1.000,0.033,0.024);
vec3 colorD = vec3(1.000,0.978,0.913);
vec3 colorE = vec3(0.123,0.078,0.323);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = (fragCoord - .5 * iResolution.xy ) / iResolution.y;

    float r = length(st) * 2.0;
    float a = atan(st.y , st.x);

    float f = abs(cos(a * 9.5)) * abs(0.4 + 0.05 * sin(iTime)) + .3;

    vec3 color = mix(vec3( smoothstep(f + 0.22,f,  r) ), 
                vec3( smoothstep(f + 0.52,1.0 -f,  r)) * colorB,
        		colorA);

    color *=  smoothstep(0.0, 0.15, length(st)) + colorC;
    color *= 1.76 * smoothstep(0., 0.1, length(st)) + colorD;
    color *= smoothstep(0.55, 0.0, length(st)) + colorE;

    fragColor = vec4(color, 1.0);
}
