void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);

	const float radiusInPixels = 50.0;

    bool onRightEdge = fragCoord.x > (iResolution.x - radiusInPixels);
    bool onLeftEdge = fragCoord.x < radiusInPixels;
    bool onTopEdge = fragCoord.y > (iResolution.y - radiusInPixels);
    bool onBottomEdge = fragCoord.y < radiusInPixels;

    vec2 topRightCorner = iResolution.xy - vec2(radiusInPixels);
    vec2 bottomRightCorner = vec2(iResolution.x - radiusInPixels, radiusInPixels);
    vec2 bottomLeftCorner = vec2(radiusInPixels);
    vec2 topLeftCorner = vec2(radiusInPixels, iResolution.y - radiusInPixels);

    const float radiusFalloffInPixels = radiusInPixels / 10.0;
    if(onRightEdge && onTopEdge)
    {
        float nearRadius = clamp(1.0 - (length(topRightCorner - fragCoord) - radiusInPixels) / 5.0, 0.0, 1.0);
        fragColor *= nearRadius;
    }
  	else if (onRightEdge && onBottomEdge)
    {
        float nearRadius = clamp(1.0 - (length(bottomRightCorner - fragCoord) - radiusInPixels) / 5.0, 0.0, 1.0);
        fragColor *= nearRadius;
    }
    else if (onLeftEdge && onTopEdge)
    {
        float nearRadius = clamp(1.0 - (length(topLeftCorner - fragCoord) - radiusInPixels) / 5.0, 0.0, 1.0);
        fragColor *= nearRadius;
    }
    else if (onLeftEdge && onBottomEdge)
    {
        float nearRadius = clamp(1.0 - (length(bottomLeftCorner - fragCoord) - radiusInPixels) / 5.0, 0.0, 1.0);
        fragColor *= nearRadius;
    }
}
