void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mid = vec2(0.5,0.5);

	float t = iTime;
	float c1 = cos(0.3*t)*sin(0.04*uv.y*t)+(distance(uv,mid));
	float c2 = 0.5+sin(distance(uv,mid)+t*2.0)*cos(sin(t*5.0));
	float c3 = pow(c1,c2);
	float c = pow(sin((c3+60.0)*uv.x)*1.0+c1,2.0-c1*0.5);
	c = sin(t+50.0*distance(uv, mid))+cos(c2);
	fragColor = vec4(0.1+c*0.4,0.3+c2*0.2,0.2+(c3*0.5)*0.4,1.0);
}
