vec4 lerp(vec4 a, vec4 b, float d){
	return a*(1.0-d)+b*d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 sum = vec4(0);
	uv.x = uv.x+0.1*sin(iTime+uv.x);
	uv.y = uv.y+0.1*sin(iTime+uv.y);
	for(int i=-4; i < 4; i++) {
		for(int j=-4; j < 4; j++) {
			sum += texture(iChannel0, uv+vec2(j, i)*0.01)*0.015625;
		}
	}

	fragColor = sum;

}
