#define DEPTH 8
#define ITERS 10
#define SCALE 2.0
#define SPEED 0.0025;

vec3 hsv(float h,float s,float v) {
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = -0.5 + fragCoord.xy / iResolution.xy;
	p.x *= iResolution.x/iResolution.y;
	vec3 c = vec3(0);
	for (int j = 0; j < DEPTH; j++) {
		float t = (iTime  + float(j)/60.0)*SPEED;
		for (int i = 0; i < ITERS; i++) {
			t += float(i);
			vec2 sc = vec2(sin(t), cos(t));
			p = vec2(p.x*sc.x + p.y*sc.y, p.x*sc.y - p.y*sc.x);
			p = abs(mod(p*1.01 + t, 2.0) - 1.0);
		}
		float h = length(p) + atan(p.y, p.x) - abs(p.x + p.y); // Changing this line will give you lots of different shapes
		c += hsv(sin(h*14.0)*0.5+0.5, 1.0, 1.0);
		p *= SCALE;
	}
	fragColor = vec4(c/float(DEPTH), 1.0);
}
