void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line
    vec4 texColor = texture(iChannel0,xy);//Get the pixel at xy from iChannel0
    vec4 texColor2 = texture(iChannel1,xy);//Get the pixel at xy from iChannel0
    float average = (texColor.r + texColor.g + texColor.b)/3.0;
    texColor.r = texColor.r + (texColor.r-average)*(sin(iTime));
    texColor.g = texColor.g + (texColor.g-average)*(sin(iTime));
    texColor.b = texColor.b + (texColor.b-average)*(sin(iTime));
    fragColor = texColor;//Set the screen pixel to that color
    if (texColor2.g < texColor2.r || texColor2.g < texColor2.b || texColor2.g < 0.4){
        fragColor = texColor2;
    }
}
