float dist(vec2 a, vec2 b) {
  // return sqrt(((a.x-b.x)*(a.x-b.x) + .01/mod(u_time,2.) - (a.y-b.y)*(a.y-b.y)));
   return sqrt(((a.x-b.x)*(a.x-b.x) + .1/(mod(iTime,4.)*exp(mod(iTime,6.))) + (1.- 3.*abs(mod(iTime,3.) -1.))* (a.y-b.y)*(a.y-b.y)));
}
vec2 rotateVec(vec2 v, float a) { 
  //return vec2(v.x*cos(a)-v.y,v.y);
  return vec2(v.x*cos(a)-v.y*sin(a),v.x*sin(a)+v.y*cos(a));
}
vec2 rotateVec(vec2 v, float a, vec2 c) { 
  return vec2((v.x-c.x)*cos(a)-(v.y-c.y)*sin(a)+c.y,(v.x-c.x)*sin(a)+(v.y-c.y)*cos(a)+c.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 c = vec2(.5,.5);
  vec2 u_texcoord = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
  vec2 vtx = rotateVec(u_texcoord,mod(iTime,6.28),c);
  float dd = dist(vtx,c);
  float freq = texture(iChannel0, vec2(dd,1.)).x;
  vec3 color = vec3(freq > u_texcoord.y,1.4-.1*mod(iTime,18.),.1*mod(iTime,10.)) * ((2.*dd) / freq);
  fragColor = vec4(color, 1.);
}
