const float iterations = 32.;

vec2 Scale(vec2 p){
	return (2.*p-iResolution.xy)/iResolution.y*1.5;
}

float mandelbrot(vec2 c){
    vec2 z = vec2(0);

    float n          = cos(iTime)*2.+4.;//change n for HIGHER POWAHSS

	for (float i=0.;i<iterations;i++){
		float rad = length(z);
		float phi = atan(z.y,z.x);

		if (rad>2.) return i;

		rad = pow(rad,n);
		phi = phi*n;

		z = vec2(
			cos(phi)*rad,
			sin(phi)*rad
		)+c;
	}
	return iterations;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){	
	vec2 scaledp = Scale(fragCoord.xy);
    vec3 color = vec3(mandelbrot(scaledp)/iterations);
	fragColor = vec4(color,1.0);
}
