float noise(float t)
{
	return texture(iChannel0,vec2(t,.0)/iChannelResolution[0].xy).y;
}
float noise(vec2 t)
{
	return texture(iChannel0,t/iChannelResolution[0].xy).y;
}
float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


vec3 lensflare(vec2 uv,vec2 pos, float timeRot, float timePuls)
{

	float ang = atan(uv.x,uv.y);

	float dist=length(uv);
    dist = pow(dist,.1);

	float f0 = 1.0/(length(uv-pos)*16.0+1.0);

    float idx = mod(ang*4.0+5.954 + timePuls * 0.1, iChannelResolution[0].x);

	f0 = f0+f0*(sin(noise(idx)*16.0)*.1+dist*.1+.8);


	vec3 c = vec3(.0);
	c = c*1.3 - vec3(length(uv)*.05);
	c+=vec3(f0);

	return c * (abs(sin(timeRot)) + 0.7);
}

vec3 cc(vec3 color, float factor,float factor2) // color modifier
{
	float w = color.x+color.y+color.z;
	return mix(color,vec3(w)*factor,w*factor2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
	uv.x *= iResolution.x/iResolution.y; //fix aspect ratio

	vec3 color = vec3(1.4,1.2,1.0) * lensflare(uv, vec2(0.0), iTime, iTime * 4.0);
	color -= .015;
	color = cc(color,.2,.1);
    float dist = 1.0 - distance(vec2(0.0), uv * 2.0);
	fragColor = vec4(color * dist,1.0);
}
