// vec4 texture(samplerXX, vec2)
// vec2 fragCoord
// vec4 fragColor
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = uv - 0.5;
    float rad = length(xy);
    float ang = atan(xy.x, xy.y);
    vec2 xy_final = vec2(rad + iTime, (1.0/ang) + iTime);
    vec4 col = texture(iChannel0, xy_final);
    col *= 1.0/rad;
    fragColor = col;
}
