vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -fragCoord.xy / iResolution.xy;
    vec3 video = texture(iChannel1, -uv).rgb;

    vec3 videoHsv = rgb2hsv(video);
    float fg = abs(videoHsv.x - 0.31);
    float blend = min(max(0.003 / (fg * fg), 0.0), 1.0);
    video = hsv2rgb(videoHsv);

	fragColor = (texture(iChannel0, uv) * blend) + (vec4(video, 1.0) * (1.0 - blend));
}

vec2 mainSound( in int samp,float time)
{
    return vec2( 0.0, 0.0 );
}
