float saturate(float x) { return clamp(x, 0.0, 1.0); }

float getMask(float d, float offset, float size)
{
    return saturate(1.0 - abs(d - offset) / size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float d = length((uv - 0.5) * vec2(iResolution.x / iResolution.y, 1.0));
    float invD = saturate(1.0 - d);
    float t = iTime;

    float c = 0.0;

    d *= 1.25;

    c += (sin(pow(d, 5.0) * 10000.0 + t) * 0.5 + 0.5) *
         getMask(d, 0.0, 0.5); 

    c = mix(c, (sin(pow(1.0 - d, 5.0) * 100.0 + t) * 0.5 + 0.5) *
         getMask(d, 0.4, 0.25), 0.5); 

    c = mix(c, (sin(pow(1.0 - d, 10.0) * 50.0 + t * 1.5) * 0.5 + 0.5) *
         getMask(d, 0.0, 0.375), 0.75);     

    c = mix(c, (sin(pow(1.0 - d, 5.0) * 300.0 + t) * 0.5 + 0.5) *
         getMask(d, 0.0, 0.25), 0.25);     

	c += pow(getMask(d, 0.325 + sin(t * 0.5) * 0.05, 0.75), 2.75) * 0.75;    

    c += (pow(sin(pow(2.0 - d, 3.0) * 100.0 + t * 2.0) * 0.5 + 0.5, 15.0)) *
         getMask(d, 0.8, 0.5) * 0.125;   

    c = pow(c * 1.125, 1.25);

	fragColor = vec4(c, c, c, 1.0);
}
