// From the book: Graphics Shaders

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 rgbColor = texture(iChannel0, vec2(uv.x, 1. - uv.y)).rgb;

    // Convert to cmy
    vec3 cmyColor = vec3(1.0) - rgbColor;

    // Calculate amount of black
    float K = min(cmyColor.x, min(cmyColor.y, cmyColor.z));

    // Segregate K
    vec3 temp = (cmyColor - vec3(K, K, K)) / (1. - K);

    vec4 cmykColor = vec4(temp, K);

    // Choose between cyan, magenta, yellow, black - respectively x, y, z, w
    // Here i choose black
	fragColor = vec4(cmykColor.www,1.0);
}
