void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*fragCoord.xy - iResolution.xy;
    uv /= max(iResolution.x, iResolution.y);

	float rad = length(uv);
	float ang = atan(uv.x,uv.y);

    vec2 tex_uv = vec2(1./rad + iTime, ang);
	vec4 tex = texture(iChannel0, tex_uv);

	fragColor = tex * rad;
}
