vec3 Circle (vec2 uv, vec2 p, float r, float stepping, vec3 col)
{    
    float d = smoothstep (r+stepping, r-stepping, length(uv-p));
    vec3 c = col*d;

	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 white = vec3 (1., 1., 1.);
    vec3 black = vec3 (.0, .0, .0);
    vec3 c = vec3 (.0,.0,.0);
    float stepping = 0.003;

    float t = iTime;
    //white *= abs(sin(iTime));

    //draw face
    vec3 face = black;
    face = Circle(uv, vec2(0, .0), .3, stepping, white);
    //draw eyes
    vec3 eyes = black;
    eyes -= 1.0*Circle(uv, vec2(-.13, .05), .05, stepping, white);
    eyes += 1.0*Circle(uv, vec2(-.13+.02*sin(t*5.), .05+.02*cos(t*3.)), .015, stepping, white);
    eyes -= 1.0*Circle(uv, vec2(.13, .05), .05, stepping, white);
    eyes += 1.0*Circle(uv, vec2(.13+.02*sin(t*3.), .05+.02*cos(t*5.)), .015, stepping, white);
    //draw mouth
    vec3 mouth = black;
    mouth = Circle(uv, vec2(.0, -.09), .15+.01*sin(t), stepping, white);
    mouth -= Circle(uv, vec2(.0, .02), .2+.01*sin(t), stepping, white);
    //reconstruc face
    c = face + min(eyes, -mouth);

	fragColor = vec4(vec3(c),1.0);
}


