void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;



    //text[0] = 

    ivec2 pixIDinQuadv = ivec2(int(fragCoord.x) % 2, int(fragCoord.y) % 2);

    int pixIDinQuad = pixIDinQuadv.y * 2 + pixIDinQuadv.x;

    switch(pixIDinQuad)
    {
        case 0: fragColor = texture(iChannel0, sqrt(uv)); break;
        case 1: fragColor = texture(iChannel1, uv*0.3); break;
        case 2: fragColor = texture(iChannel2, uv*0.7); break;
        case 3: fragColor = texture(iChannel3, vec2(1.0)-uv*uv); break;
        default: fragColor = vec4(1,0,0,1);
    };

}
