//#define P0 (20.*(.5+vec2(13,11))) // reference location
#define P0 (iResolution.x > 400. ? 20.*(.5+vec2(13,11)) : 20.*(.5+vec2(5)) ) // reference location


// --- fonts from https://www.shadertoy.com/view/llySRh

vec4 char(vec2 p, int c) { // access to the image of ascii code c
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
    //if (p.x<.25|| p.x>.75 || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5); // strange bug
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) );
}

vec4 pInt(vec2 p, float n) { //  display int4
    vec4 v = vec4(0);
    if (n < 0.) 
        v += char(p - vec2(-.5,0), 45 ),
        n = -n;

    for (float i = 3.; i>=0.; i--) 
        n /= 10.,
        v += char(p - vec2(.5*i,0), 48+ int(fract(n)*10.) );
    return v;
}

// --- display correlation

void mainImage( out vec4 O, vec2 U )
{
         O = texelFetch(iChannel0, ivec2(U),0);      // E(P), E(P²), E(P.P0)
    vec4 P = texelFetch(iChannel0, ivec2(P0),0);     // E(P0), E(P0²)
    float N = float(iFrame),
          EX = O.r/N, EY = P.r/N, EX2 = O.g/N, EY2 = P.g/N, EXY = O.b/N,
          VX = EX2-EX*EX, VY = EY2-EY*EY,
          c = (EXY-EX*EY)/sqrt(VX*VY);

    //O = O/sqrt(N) + step(length(U-P0),4.); return; // check location        

    O = c * vec4(1,0,-1,0);

    if (U.x == P0.x+.5) O.g++;
    if (U.x > P0.x) O = pow(vec4(10.),O)-1.;
    if (U.x > P0.x && U.y < .75*P0.y) O = texelFetch(iChannel1, ivec2(U),0);

#if 0                                                // test: show moments
    O-=O;
    //O.r = 14.*sqrt(EX);
    O.r = 4.*sqrt(VX);
#endif

    if (O.r>=1. || O.g>=1.) O.g++;                   // test overshoot
    O += pInt( (U/iResolution.y-vec2(.1))*16., N).r; // display N

}

// --- generate Perlin noise ( 1 realisation per time step)

// --- noise from from IQ's https://www.shadertoy.com/view/XdXGW8
vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

	return -1.0 + 2.0*fract(sin(p+iTime)*43758.5453123);
}


float noise(  vec2 p )
{
    vec2 i = floor( p ), f = fract( p ), u = f*f*(3.-2.*f);
    return mix( mix( dot( hash( i + vec2(0,0) ), f - vec2(0,0) ), 
                     dot( hash( i + vec2(1,0) ), f - vec2(1,0) ), u.x),
                mix( dot( hash( i + vec2(0,1) ), f - vec2(0,1) ), 
                     dot( hash( i + vec2(1,1) ), f - vec2(1,1) ), u.x), u.y);
}
// ---


void mainImage( out vec4 O, vec2 U )
{
    vec2 R = vec2(360);
    O  = noise (U / 20.) * vec4(1,0,-1,0);  // O.r only used for computations
    // if (O.r>=1. || O.g>=1.) O.g++;
}

// --- computes moments, and commoment with location P0

//#define P0 (20.*(.5+vec2(13,11))) // reference location
#define P0 (iResolution.x > 400. ? 20.*(.5+vec2(13,11)) : 20.*(.5+vec2(5)) ) // reference location

void mainImage( out vec4 O,  vec2 U )
{
    O = texelFetch(iChannel0, ivec2(U),0);

    float v1 = texelFetch(iChannel1, ivec2(U),0).r,
          v0 = texelFetch(iChannel1, ivec2(P0),0).r;
    O.r += v1;
    O.g += v1*v1;
    O.b += v1*v0;

}
