void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    vec3 color = texture(iChannel0, p).rgb;

    // Invert
    color = 1.0 - color;

    // Gamma
    color = pow(color, vec3(0.157));

    // Vigneting
    float l = length(p - vec2(0.5, 0.5));
    color *= 1.0 - pow(l, 2.0);

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 p = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
 	p.x *= aspect;

    vec3 color = vec3(1.0, 0.3,0.1);

    vec2 q = p - vec2(0.7 * aspect, 0.6);
    vec2 q1 = p - vec2(1.0, 0.5);

    float r = 0.15 + 0.02 * cos(iTime);

    r += 0.05;

    color *= smoothstep(r, r +0.01, length(q) );

    float w = 0.15;
    w += 0.01 * cos( 20.0 * p.x + iTime * 5.0) + 0.01 * sin(10.0 * p.x);

    color *= smoothstep(w, w + 0.01, p.y);

	fragColor = vec4(color,1.0);
}
