renderpass.code:
float grayscale(vec3 color) {
    return (color.r + color.g + color.b) / 3.;
}

float snoise(in vec2 co){
    return fract(sin(dot(co.xy, iTime * vec2(12.9898,78.233))) * 43758.5453);
}

float ramp(float y, float start, float end) {
	float inside = step(start, y) - step(end, y);
	float fact = (y - start) / (end - start) * inside;
	return (1. - fact) * inside;
}

float scanline(vec2 coord) {
    return sin(coord.y + 40. * mod(iTime, 100.)) * 0.02;
}

float scanline2(vec2 coord) {
	float noi = snoise(coord);
	float line = ramp(mod(coord.y * 4. + iTime / 2. + sin(iTime + sin(iTime * 0.63)),1.),0.5,0.6);
	return line * noi;
}

vec2 crt(vec2 coord, float bend) {
	coord = (coord - 0.5) * 2.0;

	coord *= 1.05;	

	coord.x *= 1.0 + pow((abs(coord.y) / bend), 2.0);
	coord.y *= 1.0 + pow((abs(coord.x) / bend), 2.0);

	coord  = (coord / 2.0) + 0.5;

	return coord;
}

vec3 sampleSplit(sampler2D tex, vec2 coord){
	vec3 frag;
	frag.r = texture(tex, vec2(coord.x - 0.005 * sin(iTime), coord.y)).r;
	frag.g = texture(tex, vec2(coord.x                              , coord.y)).g;
	frag.b = texture(tex, vec2(coord.x + 0.005 * sin(iTime), coord.y)).b;
	return frag;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 tint = vec3(.7,1.,.7);
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 crtCoords = crt(uv, 3.2);
    vec3 color = sampleSplit(iChannel0, crtCoords);
	vec2 screenSpace = crtCoords * iResolution.xy;

    color = vec3(grayscale(color));
    color += scanline(screenSpace);
    color += color * scanline2(crtCoords);
    color *= tint;

    bool black = (crtCoords.x >= 0.) && (crtCoords.y >= 0.) && (crtCoords.x <= 1.) && (crtCoords.y <= 1.);
    color *= float(black);


	fragColor.rgb = color;
}
