float inGap(float a, float b, float t) {
	if(t >= a && t <= b) return 1.0;
    return 0.0;
}

float rect(vec2 uv, float x_1, float x_2, float y_1, float y_2) {
	float inX = inGap(x_1, x_2, uv.x);
    float inY = inGap(y_1, y_2, uv.y);

    return inX * inY;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	float c = rect(uv, 0.4, 0.6, 0.4, 0.6);

    fragColor = vec4(vec3(c),1.0);
}
