void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
    uv.x+=1.5;  
    uv.y += cos(uv.x*(8.0  + 2.7 * (0.0 + cos(iTime * 0.1))))*0.15;
    uv.y*=-20.0 + 33.0 * sin(iTime * 0.02);
    float r = sin(iTime * 0.1) * 2.0,
    e = uv.x-iTime*0.1,
    t = e + abs(uv.y),
   //s = step( fract(t*r),f);
   // s = smoothstep(1.5,0., (fract(t*r)-f)/fwidth(t*r-f)); 
   //O = vec4(s);
   f = (1.0+sin(uv.x*2.0 * cos(iTime *0.1)))*0.2+0.1, 
   d = f - abs(fract(t*r) - .1)*0.9,
   s = smoothstep(0., .25/max(d + .01, 0.), d + .1);    
   O = vec4(vec3(sqrt(s*0.1)),0.0);
}
