void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 solidGrey = vec4(0.2, 0.2, 0.2, 0.5);
    vec4 red = vec4(1, 0, 0, 0);

    vec2 theCircleCenter = vec2(100, 100);
    vec2 dist = fragCoord - theCircleCenter;
    float len = dist.x*dist.x + dist.y*dist.y;

    fragColor = solidGrey;
    if (len < 1000.0) {
        fragColor = red;
    }

    if (distance(fragCoord, vec2(300, 200)) < 10.0) {
        fragColor = vec4(0,1,0,0);
    }

    fragColor.r += sin(fragCoord.x / 50.0 + iTime*2.0)/2.0;
    fragColor.g += sin((fragCoord.x + 0.5*fragCoord.y) / 60.0 + iTime * 1.5) / 1.5;


}
