// display: splatting the particles.
//
//   we only have to test the coordinates in the current NxN tile,
//   + neighbor tiles if the sprites are larger than 1 pixel.

#define N 8. // tile size = NxN   ( change also in BufA ).
#define r 3. // sprite radius     ( must be <= N )

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy,
        iU = floor(U/N)*N;    // tile coordinate
    O -= O; 
#if 1   
    for (int j = -1; j <= 1; j++)
        for (int i = -1; i <= 1; i++) { // check neighbor tiles (because sprites > 1 pix)
            vec2 d = vec2(i,j);
            for (float y=0.; y<N; y++)  
                for (float x=0.; x<N; x++) { // splat all the particles found in the tile
                    vec4 T = texture( iChannel0,( iU + N*d + vec2(x,y)+.5 )/R );
                    if (T.xy==vec2(0)) continue;      // void cell
                    O += (1.-O.a) * step( length(T.xy*R-U) , r)    // splat a disk
                                  * max(vec4(.2+T.zw,.2-T.z,1.),0.);    // color
                }
        }
 // O += (1.-O.a)*.2;
#else                                   // --- test
    for (float y=0.; y<N; y++)  
        for (float x=0.; x<N; x++)
             O += vec4( texture( iChannel0,( iU + vec2(x,y)+.5 )/R ).xy != vec2(0) )*.1;

#endif
}

#define N 8. // tile size = NxN    ( change also in Image ).
#define k .3  // nbr particles = k*resolution

#define rnd(x)  fract(3e4*sin((x)*mat2(1,13.51,73.37,-57.17)))
#define srnd(x) (2.*rnd(x)-1.)

#define T(d)   texture( iChannel0, fract ( (iU +N*d +fU) / R ) ) // coords in neighbor tile
#define out(d) floor( ( T(d).xy*R - (iU+N*d) ) / N )               // where is particle leaving

#define keyClick(ascii) (texture(iChannel2,vec2((.5+float(ascii))/256.,0.25)).x > 0.)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, Z=R-R,
        iU = floor(U/N)*N, fU = U-iU;

    if (iFrame==0 || keyClick(32)) { // --- initialization
        if (rnd(U*1.1).x < k) {             // keep k % of particles        
            O.xy = ( iU + rnd(U)*N )/R;     // random position
            O.zw = srnd(U+1.);              // random speed
        } else O -= O;                      // otherwise void cell
        return;
    }

    O = T(0.);                       // --- check tile change in neighborhoods NxN

#if 0
    if (out(0.) != Z) O -= O;               // the particle leaves the tile : del
#else // rought collision management: tile transfer only if slot is free 
    vec2 d = out(0.); if (d != Z && T(d).xy==Z ) O -= O;

    if (O.xy!=Z) return;                    // receiver not free
#endif    
    for (int j = -1; j <= 1; j++)
        for (int i = -1; i <= 1; i++) {     // check neighbor tiles
            vec2 d = vec2(i,j);
            if (d==Z) continue;
            if (T(d).xy==Z) continue;       // void cell
            if (out(d) == -d) O = T(d);     // the particle enters the tile
            // problem : more than 1 transfer from neighbors can occurs
           }
}

// advect particles  
//
// .xy = pos, .zw = velocity

#define dV iTimeDelta*.2  // must be < N/R.y

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O = texture(iChannel0,U/R);
    O.xy = fract( O.xy + O.zw*dV);
}
