vec2 trans(vec2 v, vec2 x) {
    return ((1.0 + 2.0 * dot(v, x) + dot(x, x)) * v + (1.0 - dot(v, v)) * x) /
        (1.0 + 2.0 * dot(v, x) + dot(v, v) * dot(x, x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv.x += iTime * 0.1;
    vec2 origin = texture(iChannel1, vec2(iTime * 0.005, 0.0)).rg * 2.0 - vec2(1.0);

    //uv = trans(0.8 * vec2(sin(iTime * 2.0), cos(iTime)), 2.0 * uv - vec2(1.0 ,1.0));
    uv = trans(origin, 4.0 * (2.0 * uv - vec2(1.0 ,1.0)));
    //uv = trans(uv, 0.25 * vec2(cos(iTime), sin(iTime)));//trans(2.0 * uv - vec2(1.0, 1.0), vec2(iTime * 5.0, 0.0));

    uv = 0.5 * uv + vec2(0.5, 0.5);
    uv.x /= 5.0;
    vec4 color = texture(iChannel0, uv);
    //color.g *= sin(iTime * 10.0);
	fragColor = color;
}
