// Used to have https://soundcloud.com/admbmb/ishtzu but stopped working
#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =
        ((fragCoord.xy / iResolution.xy) * 2. - 1.);
    	// + vec2(sin(iTime), cos(iTime)) * sin(iTime * 2.33) * .5
        // * texture(iChannel0, vec2(.1, 0.)).x;

    float a = atan(uv.x, uv.y * -1.);
    float a2 = a - iTime / 4.;
    float am = a / M_PI;

    float m = fract(a2 / M_PI * 2.);
    float n = sin(m * M_PI * (4. + 2. * ceil(sin(iTime) * 1.2)));
    float l = length(uv);
    float p = l * (l + .7) * l * n * (sin(iTime) + 2.);

    float s = texture(iChannel0, vec2(abs(am) + .01, 0.)).x;
    float s2 = s * 1.3;
    vec4 c = vec4(
        s * (.5 + .5 * cos(s2 * M_PI - M_PI)) * 2.,
        s * (.5 + .5 * cos(s2 * M_PI)),
        s * (.5 + .5 * cos(s2 * M_PI * 2. - M_PI)) * 2.,
        1.);
    fragColor = c * p;
}
