#define N 60. // numver of particles = NxN
#define r 3. // sprite radius  

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    O -= O;

    for (float y=0.; y<N; y++)
        for (float x=0.; x<N; x++) {
            vec4 T = texture(iChannel0,(.5+vec2(x,y))/R);
            O += step(length(T.xy*R-U),r)*vec4(T.zw,-T.zw);
        }
}

#define rnd(x) fract(3e4*sin((x)*mat2(1,13.51,73.37,-57.17)))
#define srnd(x) (2.*rnd(x)-1.)

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    if (iFrame==0) {
        O = vec4(rnd(U), srnd(U+1.));
        return;
    }
    O = texture(iChannel0,U);
    O.xy = fract(O.xy+O.zw*iTimeDelta*.2);
}
