void mainImage(out vec4 fragColor, in vec2 fragCoord ){
  fragColor = step(
    pow(
      mod(
        reflect(
          vec4(
            (1.2 + 1.2),
            (1.3 * (0.7 - 0.6)),
            iTime,
            0.3
          ),
          exp2(
            vec4(
              1.4,
              0.4,
              step(1.1, 0.8),
              1.0
            )
          )
        ),
        normalize(
          reflect(
            fract(
              pow(
                vec4(
                  1.8,
                  (fragCoord.x / iResolution.x),
                  0.3,
                  1.2
                ),
                vec4(
                  0.0,
                  ((fragCoord.y / iResolution.y) - iTime),
                  atan(
                    (fragCoord.x / iResolution.x),
                    0.2
                  ),
                  (1.3 * 1.0)
                )
              )
            ),
            fract(
              vec4(
                (fragCoord.y / iResolution.y),
                1.7,
                1.8,
                min(
                  0.0,
                  step(
                    1.6,
                    (0.3 * 1.8)
                  )
                )
              )
            )
          )
        )
      ),
      vec4(
        0.9,
        (0.3 / 1.1),
        (0.7 / ceil(0.7)),
        iTime
      )
    ),
    vec4(
      0.7,
      (fragCoord.x / iResolution.x),
      0.6,
      1.5
    )
  );
}
