// application to stars color: https://www.shadertoy.com/view/XdsGWs

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    O = vec4(1,.6,.3,0);         // base color
    if (U.y>.2) O *= U.x/O.b;    // from dark to saturated up to full white
    if (U.y<.1) O = U.xxxx;
 // O = pow(O,vec4(1./2.2));     // to sRGB
}
