void mainImage( out vec4 fragColor, in vec2 fragCoord ){	
	vec3 col;
	float l;
    float z=iTime;
	for(int i=0; i<10; i++) {
		vec2 uv;
        vec2 p=fragCoord.xy/iResolution.xy;
		uv=p;
        float fft  = texture( iChannel0, vec2(uv.x,1.25) ).x; 
        float wave = texture( iChannel0, vec2(uv.x,1.75) ).x*.55;
		p-=.5;
		p.x*=iResolution.x/iResolution.y;
		z+=.15;        
		l=length(wave);
		uv+=p/l*(sin(z)+1.)*abs(sin(l*9.-z*2.));
		col[i]=.09/length(abs(mod(uv,1.)-.5));
	}
	fragColor=vec4(col/l, 1.0);
}
