// void main (void) {
// ShaderToy Specific Input Settings
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float u_time = iTime;
    vec2 u_mouse = iMouse.xy;
    vec2 u_resolution = iResolution.xy;
//.

	// A looping gradient. The bottom white intensifies the lower the mouse gets, the color loops faster the further the mouse moves right.

	// 
	vec2 mouse_n = u_mouse.xy / u_resolution;
	vec2 pixel_n = gl_FragCoord.xy / u_resolution; 

	float loopSpeed = max(0.2, mouse_n.x * 20.0);
	float time = u_time * loopSpeed * 1.0;

	// Create a flat color altered by time
	float r = (sin(0.19+time*0.42)+1.0)/2.0;
	float g = (sin(0.3+time*0.11)+1.0)/2.0;
	float b = (sin(time*0.57)+1.0)/2.0;

	// Create a brighter additional glow the cloer to the bottom
	float pixelAddition_intensity = max(0.2, 1.0 - mouse_n.y);
	float pixelAddition = (1.0-pixel_n.y) * pixelAddition_intensity;
	// vec3 color = vec3(r,g,b) + vec3(pixelAddition, pixelAddition, pixelAddition);
	// gl_FragColor = vec4(color, 1.0);


	vec4 color = vec4(pixel_n.x, pixel_n.y, mouse_n.x, 1.0-mouse_n.y);

	// ShaderToy Specific Settings
    fragColor = vec4(color);
	// fragColor = vec4(color, 1.0);
	// .
}
