#define ShaderTime iTime

float PI = 3.14159265359;

float saw(float x)
{
    return acos(cos(x))/3.14;
}
vec2 saw(vec2 x)
{
    return acos(cos(x))/3.14;
}

vec2 spiral(vec2 uv)
{
    float turns = 4.0+float(int(1.0*saw(ShaderTime*.321)));
    float r = pow(log(length(uv)+1.)+1.0, 1.25+.25*sin(ShaderTime*.7654))+ShaderTime;
    float theta = atan(uv.y, uv.x)*turns;
    return vec2(saw(r*PI+theta/turns*float(int(1.0*saw(ShaderTime*.543)))), saw(theta+ShaderTime*1.1));
}

vec4 draw(vec2 uv)
{
	if(abs(abs(saw(uv.x*(1.5+sin(ShaderTime*.654321))*PI+ShaderTime*.7654321)*2.0-1.0)-abs(uv.y)) < .5)
		return vec4(1.0-abs(abs(saw(uv.x*(1.5+sin(ShaderTime*.654321))*PI+ShaderTime*.7654321)*2.0-1.0)-abs(uv.y))/.5)*uv.x;
	else
		return vec4(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = (22.0+11.0*sin(ShaderTime*.1234))*PI;
    uv = spiral(uv*scale-scale/2.0);
    vec4 screen = draw(uv)*clamp(saw(ShaderTime*1.45678), 0.0, 1.0);

    uv = saw(uv*PI*2.0);

    scale = (40.0+20.0*sin(ShaderTime*.234))*PI;
    uv = spiral(uv*scale-scale/2.0);
    screen += draw(uv)*clamp(saw(ShaderTime*1.2345678), 0.0, 1.0);

    screen.x *= 2.0*PI;
    fragColor = clamp(screen.x, 0.0, 1.0)*vec4(vec3(sin(ShaderTime*5.0+0.0+screen.x),
                                                    sin(ShaderTime*5.0+4.0*PI/3.0+screen.x),
                                                    sin(ShaderTime*5.0+2.0*PI/3.0+screen.x))*.5+.5,
                                               1.0);
}
