void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;

	vec4 col = texture(iChannel0, p);

    //int nFrame = 4;
    int nFrame = int( (iMouse.x/iResolution.x) * 20.0 );

    if ( (iFrame % nFrame) >= nFrame/2) {

    col = vec4(1.) - texture(iChannel0, p); // intense strobe
    //col = vec4( clamp(dot(col, col), 0., 1.) ); // chill strobe  

    }
    col = vec4( (col.x + col.y + col.z) / 3.);

	fragColor = col;
}
