#define gold 1.0/1.618033
void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f/iResolution.xy,
     r =  floor(mod(f*0.5,1.5)); 
    float T = iTime*1.,
    t = sin(T),
    e = cos(T) < 0. ? uv.y : uv.x,
    a = mix(r.x+r.y,r.x-r.y,e-t),
    b = mix(pow(r.x, r.y),r.x*r.y,e+t),
    tt = sin(T*0.2),
    grid = uv.x > gold ? a : b;
    if( uv.x < 0.17) grid = t;
    if( uv.x > 0.99) grid = 1.0-t;
    O = vec4(grid);
}

vec2 mainSound( in int samp, float time )
{
    float t = cos(0.5+time);
    return vec2( sin(6.2831*140.0*t)*exp(-3.0*t) );
}
