lowp vec3 color;
lowp float opacity;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    color = vec3(0.69,0.44,0.02);
    opacity = 1.0;
    lowp vec4 c;
    lowp vec4 Ca = texture(iChannel0, uv);
    lowp vec3 base = Ca.rgb;
    lowp vec3 blend = color;

    //original
    c = vec4(mix(1.0 - 2.0 * (1.0 - base) * (1.0 - blend), 2.0 * base * blend, step(base, vec3(0.5))), Ca.a);
    //simplified
    c = vec4(2.0 * base * blend, 1.0);

    c.a *= opacity;

    fragColor = c;
}
