void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2( fragCoord.xy / iResolution.xy );

    float linePos;
    float offset = 1./180.;
    float lineWidth;

 	vec3 col = vec3 (0.);

    const float hourWidth = 1./12.;
	for(float i=0.0; i<1.0; i+=hourWidth) {
		// "i" is the line coordinate.
		if(abs(uv.x - offset - i)<0.004 && (uv.y > 0.95 || uv.y < 0.05)) col = vec3(1.);

	}

    const float minWidth = 1./60.;
	for(float i=0.0; i<1.0; i+=minWidth) {
		// "i" is the line coordinate.
		if(abs(uv.x - offset - i)<0.002 && (uv.y > 0.975 || uv.y < 0.025)) col = vec3(1.);

	}

    float mils = fract(iDate.w)/60.;
	float secs = (mod( floor(iDate.w),        60.0 ))/60.;
	float mins = (mod( floor(iDate.w/60.0),   60.0 ))/60.;
	float hours =(mod( floor(iDate.w/3600.0), 12.0 ))/12.;

    secs+=mils;

    //hour line
    linePos = hours;
    lineWidth = 0.004;
    if(abs(uv.x - offset - linePos)<lineWidth) col =vec3(0.,1.,0.);

    //minutes line
    linePos = mins;
    lineWidth = 0.002;
    if(abs(uv.x - offset - linePos)<lineWidth) col =vec3(1.,0.,0.); 
    linePos = secs;

    //seconds line
    lineWidth = 0.001;
    if(abs(uv.x - offset - linePos)<lineWidth) col =vec3(0.,0.,1.);


    fragColor = vec4(col,1.0);
}
