// inspired from https://www.youtube.com/watch?v=Jri0del_6t4


// Gabor kernel
#define G(U,a,v) exp(-.5*dot(U,U)*1e3) * sin( 100.*dot(U,sin(a+vec2(1.57,0))) -(v)) 


void mainImage( out vec4 O, vec2 U )
{
	U = (U+U - (O.xy=iResolution.xy) ) / O.y;
    O -= O;

    for (float i=-8.; i<9.; i++)   
        for (float j=-4.; j<5.; j++)    
            O +=   G( U-vec2(i*.2 , j/5.), .8  , 10.*sin(.5*j+.2)*sin(6.*iDate.w+.3*i));
         // O +=   G( U-vec2(i*.2 , j/5.), .8  , i*j*sin(3.*iDate.w));

	O = .5 + .5*O; 
}
