void mainImage( out vec4 c, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy*.5) / iResolution.y;
    float t = 60. + mod(sin((iTime)*.18+length(uv*sin(5.*uv.y+uv.x))),.1)*5.;

    c = vec4(1.3-uv.y*.5, 1., .3 * 1.5/sqrt(sin(t*5.+uv.x*40.)+cos(uv.y*20.)) , 1.);

    for(float f = 4.0; f < 10.; f += 2. )
		c += vec4( .06/sqrt(sin(uv.x*f)+cos(uv.y*f)+tan(f/t+t) ) );

	c -= (length(c) < 1.65+uv.x+uv.y) || (mod(t*.1,.1) < .005) ? 1.5 / c : c;
}
