int heart[8] = int[](0, 16, 56, 124, 254, 254, 238, 68);

vec4      red = vec4(0.502, 0.082, 0.082, 1.0);
vec4   salmon = vec4(1.000, 0.353, 0.353, 1.0);
vec4     pink = vec4(0.945, 0.471, 0.639, 1.0);
vec4   orange = vec4(1.000, 0.286, 0.000, 1.0);
vec4 ltorange = vec4(1.000, 0.514, 0.322, 1.0);
vec4   yellow = vec4(1.000, 0.871, 0.133, 1.0);
vec4    black = vec4(0.000, 0.000, 0.000, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float zoom = 1.0;
    int xorig = 5;
    int yorig = 10;
    int xsp = 10*(int(iTime) % 10) + 8;  // Must be > 8.
    int ysp = 10*(int(iTime) % 10) + 8;  // Must be > 8.

    fragCoord = fragCoord - vec2(xorig, yorig);
    if (fragCoord.x < 0.0 || fragCoord.y < 0.0) {
        fragColor = black;
        return;
    }
    fragCoord = fragCoord/zoom;


    int i = int(fragCoord.y) % ysp;
    int pointmask = i > 8 ? 0 : heart[i];
    int j = int(fragCoord.x) % xsp;
    for (int r = 0; r < j; r++) {
      pointmask = pointmask/2; //left shift.
    }

    if (pointmask % 2 == 1) {
        int c = int(fragCoord.y) / ysp % 6;
        switch (c) {
           case 0:
             fragColor = yellow;   
             break;
           case 1:
             fragColor = ltorange;
             break;
           case 2:
             fragColor = orange;
             break;
           case 3:
             fragColor = pink;
             break;
           case 4:
             fragColor = salmon;
             break;
           case 5:
             fragColor = red;
             break;
        }
    } else {
        fragColor = black;
    }
}
