#define RADI 5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 pixel;
    float[5] peso = float[](0.382928/2.,	0.241732,	0.060598,	0.005977,	0.000229);
    float high=1.0/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
	fragColor=texture(iChannel0,uv);
    vec4 color = vec4(0.f,0.f,0.f,1.f);

     for ( int i = 0 ; i <= RADI ; i++){   

        //select the pixel
       	pixel += texture(iChannel0 ,  vec2(uv.x,uv.y +float(i) * high ))* peso[i];
     	//select oposite pixel
    	pixel += texture(iChannel0 ,  vec2(uv.x, uv.y-float(i) * high ))* peso[i];          
    }
	fragColor = pixel;

}

