#define VO void
#define BO bool
#define FL float
#define V2 vec2
#define V3 vec3
#define V4 vec4

#define PI 1.5708
#define t  iTime

#define R ( iResolution.xy )

// some globals
V4
    O;		// output color

V2
    N,   // ( 0 ,0 ) bis ( 1, 1 )
    S;   // ( 0 ,0) bis ( R.x / R.y, 1 )


VO
    init( V2 i ) {

        S = V2( R.x / R.y, 1. ) * ( 2. * i / R - 1. );

        //O = V4( 0. );
    }
#define B ( V2( 1 ) + .5 * V2( cos( PI * ( j.x * j.y +
#define M * t ) ), sin( PI * ( j.y * j.x + 
#define E * t ) ) ) ) * h
VO
    work( ) {

        FL
            f = 11. + 10. * cos( .05 * t ),
            h = .5 / f;

        V2
            j = floor( f * S ) / f,
            i = S - j,
            x = B 5.5 M 5.5 E,
            y = B 5.2 M 5.2 E,
            z = B 5.0 M 5.0 E;

        V2
            r = i - x,
            g = i - y,
            b = i - z;

        V3
            c = ( 10. + 9. * sin( .235 * t ) ) * V3( dot( r, r ), dot( g, g ), dot( b, b ) );

        O.rgb= mix(
            1. - 128. * c,
            .5 * pow( c, V3( -.05 ) ),
            .5 + .5 * sin( .03 * t ) );
    }

VO
    finish( out V4 o ) {

        o = clamp( O, 0., 1. );
    }

VO
    mainImage( out V4 o, in V2 i ) {

        init( i );

        work( );

        finish( o );        
    }
