precision highp float;
#define STEPS 256



void mainImage( out vec4 f, in vec2 p )
{
    float n = 0.;
    vec2 c = (2.*p - iResolution.xy/iResolution.y)*2.0-1.;
    c *= .01;
    c.x -= .5;
    float t = (sin(-iTime*.05)+1.)*20. + 0.02;
    c *= .00125 * t;
    c -= vec2(1.394, 0.0055);// + sin(vec2(1.,.2) + iTime) * .01;
	vec2 z = vec2(0.0);

    for( int i=0; i<STEPS; i++ )
    {
        z = vec2( z.x*z.x - z.y*z.y, 2.*z.x*z.y ) + c;
        if(length(z)>4.) break;
        n++;
    }

    //f = .5 + .5*cos( vec4(3,4,11,0) + .05*(n - log2(log2(dot(z,z)))) );
    vec4 col = vec4(3,4,11,0);
    //float g = n - log2(log2(dot(z,z)));
    float cc = (n - log2(dot(z,z)));
    f = vec4(.5 + .5*cos(iTime+col + .05*cc));
}
