void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float r = texture(iChannel0, uv).r;
    float g = texture(iChannel1, uv).r;
    float b = texture(iChannel2, uv).r;

    fragColor = vec4(r, g, b, 1);
}

#define FEED_RATE chan * 0.05
#define KILL_RATE chan * 0.15
#define DIFF vec2(0.2,0.05)
#define ZOOM 1.0
#define RADIUS 0.005

float getChannel(sampler2D sam, vec2 uv)
{
    vec4 c = texture(sam,uv);
    return max(min(c.r, c.g), min(max(c.r, c.g), c.b)); // https://github.com/Chlumsky/msdfgen
}

vec2 cell(vec2 fragCoord, vec2 pixel, vec2 dir, float scale, sampler2D sam, vec2 size)
{
    return texture(sam, fract((fragCoord + pixel * dir) / size.xy)).rg * scale;
}

vec2 laplacian2D(vec2 fragCoord, vec2 dir, float a, float b, sampler2D sam, vec2 size) 
{
    float st = 1.;
    a /= 4.;
    b /= 4.;
    return 
        cell(fragCoord, vec2(0., -st), dir, a, sam, size) +
        cell(fragCoord, vec2(0., st), dir, a, sam, size) +
        cell(fragCoord, vec2(st, 0.), dir, a, sam, size) +
        cell(fragCoord, vec2(-st, 0.), dir, a, sam, size) +
        cell(fragCoord, vec2(-st, -st), dir, b, sam, size) +
        cell(fragCoord, vec2(-st, st), dir, b, sam, size) +
        cell(fragCoord, vec2(st, -st), dir, b, sam, size) +
        cell(fragCoord, vec2(st, st), dir, b, sam, size) -
        cell(fragCoord, vec2(0., 0.), dir, 1., sam, size);
}

vec4 calc(vec2 vFragCoord, vec2 vSize, int vFrame, vec4 vMouse, sampler2D vBuffer, sampler2D vPattern, int vChan)
{
    vec4 res = vec4(0);

    vec2 uv = vFragCoord / vSize;

    if (vFrame < 10)
    {
        res = vec4(1,0,0,1);
    }
    else
    {
        float a = float(vFrame) * 0.1;

		vec2 dir = normalize(vec2(a+cos(a),a-sin(a)));

        dir = vec2(1);

        float chan = texture(vPattern, uv)[vChan];

        vec2 diffusionCoef = DIFF;
        float feedCoef = FEED_RATE;
        float killCoef = KILL_RATE;

        vec2 ab = cell(vFragCoord, vec2(0,0), vec2(0,0), 1., vBuffer, vSize);
        vec2 lp = laplacian2D(vFragCoord, dir, .5, .5, vBuffer, vSize);

        float reaction = ab.x * ab.y * ab.y;
        vec2 diffusion = diffusionCoef * lp;
        float feed = feedCoef * (1. - ab.x);
        float kill = (feedCoef + killCoef) * ab.y;

        ab += diffusion + vec2(feed - reaction, reaction - kill);

    	res = vec4(clamp(ab,-1.,1.),ab*chan);
    }

    if (vMouse.z > 0.0)
	{
		vec2 mo = vMouse.xy / vSize / ZOOM;
    	if (length(uv-mo) < RADIUS)
			res = mix(vec4(0,1,0,1), vec4(1,0,0,1), length(uv-mo)); 
	}

    return res;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = calc(fragCoord, iResolution.xy, iFrame, iMouse, iChannel0, iChannel1, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = calc(fragCoord, iResolution.xy, iFrame, iMouse, iChannel0, iChannel1, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = calc(fragCoord, iResolution.xy, iFrame, iMouse, iChannel0, iChannel1, 2);
}
