void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    vec4 b = but(uv, iTime);
    fragColor = 
        texture(iChannel0, uv)
        *smoothstep(-5.0, -1.0 ,b.w)
        *vec4(vec2(1.0)+b.xy, 0.0,1.0)
        ;
}

vec4 but(vec2 uv, float time) {
    uv-= 0.5;
    uv*= 1.5;
    uv.x*=1.5;
    uv.x *= 1.0 -sin(time*4.)*.3;
    vec2 p =uv*20.0;

   	float r = length(p);
	float t = atan(p.y, p.x);

    float b = 
        7. - 0.5*sin( 1.*t) 
           + 2.5*sin( 3.*t) 
           + 2.0*sin( 5.*t) 
           - 1.7*sin( 7.*t) 
           + 3.0*cos( 2.*t) 
           - 2.0*cos( 4.*t) 
           - 0.4*cos(16.*t) - r;

    vec2 g = normalize(p);
    vec2 c = g*smoothstep(-5.0, -5.0, b);
    c*=smoothstep(10.0,-10.0, b);
    return vec4(c, 0.0, b);
}

vec4 stars(vec4 o,vec2 i, float t) {
    return smoothstep(0.0,1.0,90.0*fract(dot(sin(i+t),i))-89.1-o);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 v = but(uv, iTime).xy;
    v = normalize(v);
    float c = cos(iTime*4.0);
    v += v.x>0.0 ? vec2(v.y,-v.x)*c : vec2(-v.y, v.x)*c;
    v = normalize(v);
    fragColor += 
        texture(iChannel0, uv-v/400.0)*0.99
        +stars(fragColor, fragCoord, iTime/4000.0);
    //fragColor *= 0.99;
}
